function ClickTracking(){}ClickTracking.init=function(){var a=$("#analytics-clickCharts").find("#clicks");a.length>0&&new Chart(a,{type:"line",data:{datasets:[{label:a.data("clicks-title"),data:a.data("clicks"),lineTension:.1,fill:!1,backgroundColor:"#4BC0C0",borderColor:"#4BC0C0"},{label:a.data("sum-title"),data:a.data("sum"),lineTension:.1,fill:!1,backgroundColor:"#FF3300",borderColor:"#FF3300"},{label:a.data("avg-title"),data:a.data("avg"),lineTension:.1,fill:!1,backgroundColor:"#00CC00",borderColor:"#00CC00"}]},options:{legend:{position:"bottom",display:!1},scales:{xAxes:[{type:"time",time:{unit:"day",tooltipFormat:"D. MMMM YYYY",round:a.data("clicks").length<=1?"":"day"}}],yAxes:[{ticks:{beginAtZero:!0}}]}}})},$(document).ready(function(){ClickTracking.init(),$('#analytics-clickCharts input[name="interval"], #analytics-clickCharts select').on("change",function(){$(this).closest("form").submit()})});
$(document).ready(function(){$("#analytics-pageVisits").find('input[name="interval"]').on("change",function(){$(this).closest("form").submit()})});
function VisitsCharts(){}VisitsCharts.init=function(){var t=$("#analytics-visitsCharts"),a=t.find("#visitsByDay");a.length>0&&new Chart(a,{type:"line",data:{datasets:[{label:a.data("title"),data:a.data("data"),lineTension:.1,backgroundColor:"rgba(75,192,192,0.4)",borderColor:"rgba(75,192,192,1)",pointBackgroundColor:"rgba(75,192,192,1)",pointHoverBackgroundColor:"rgba(255,255,255,1)",pointHoverBorderColor:"rgba(75,192,192,1)",pointHoverBorderWidth:1,pointRadius:4}]},options:{legend:{position:"bottom"},scales:{xAxes:[{type:"time",time:{unit:"day",tooltipFormat:"D. MMMM YYYY",round:a.data("data").length<=1?"":"day"}}],yAxes:[{ticks:{beginAtZero:!0}}]}}});var o=t.find("#visitsByHour");if(o.length>0){for(var i=[],r=0;r<24;r++)i.push(r);new Chart(o,{type:"bar",data:{labels:i,datasets:[{label:o.data("title"),data:o.data("data"),backgroundColor:"rgba(75,192,192,0.4)",borderColor:"rgba(75,192,192,1)",pointBackgroundColor:"rgba(75,192,192,1)",pointHoverBackgroundColor:"rgba(255,255,255,1)"}]},options:{legend:{position:"bottom"},scales:{yAxes:[{ticks:{beginAtZero:!0}}]}}})}},$(document).ready(function(){VisitsCharts.init(),$("#analytics-visitsCharts").find('input[name="interval"]').on("change",function(){$(this).closest("form").submit()})});