function ClickTracking(){}ClickTracking.init=function(){var a=$("#analytics-clickCharts #clicks");a.length>0&&new Chart(a,{type:"line",data:{datasets:[{label:a.data("clicks-title"),data:a.data("clicks"),lineTension:.1,fill:!1,backgroundColor:"#4BC0C0",borderColor:"#4BC0C0"},{label:a.data("sum-title"),data:a.data("sum"),lineTension:.1,fill:!1,backgroundColor:"#FF3300",borderColor:"#FF3300"},{label:a.data("avg-title"),data:a.data("avg"),lineTension:.1,fill:!1,backgroundColor:"#00CC00",borderColor:"#00CC00"}]},options:{legend:{position:"bottom",display:!1},scales:{xAxes:[{type:"time",time:{unit:"day",tooltipFormat:"D. MMMM YYYY",round:a.data("clicks").length<=1?"":"day"}}],yAxes:[{ticks:{beginAtZero:!0}}]}}})},$(document).ready(function(){ClickTracking.init(),$('#analytics-clickCharts input[name="interval"], #analytics-clickCharts select').on("change",function(a,t){$(this).closest("form").submit()})});
